<?php$message_subject = sprintf(esc_html(__("Enter league %s",'victorious')), str_replace('&#39;', "'", $league['name']));$message_body = sprintf(esc_html(__('Thank you for entering league %s Please verify the list that you picked below:','victorious')),$league['name']).'<br>';if($picks != null){    foreach($picks as $k => $pick)    {        if($league['gameType'] == 'PICKSQUARES'){            $message_body.= ($k + 1).'. '.$pick."<br/>";        }        else if($league['gameType'] == VICTORIOUS_GAME_TYPE_PORTFOLIO){            $message_body.= ($k + 1).'. '.$pick['name'].' - '.$pick['quantity'].' '.esc_html(__("coin(s)",'victorious'))."<br/>";        }        else{            $message_body .= ($k + 1).'. '.$pick['name']."<br/>";        }    }}?>