<?php$message_subject = sprintf(esc_html(__("User %s entered to contest %s",'victorious')),$user_name,str_replace('&#39;', "'", $league['name']));$message_body = sprintf(esc_html(__("User %s entered to contest %s" ,'victorious')),$user_name ,$league['name']). "<br>";if($picks != null){    foreach($picks as $k => $pick)    {        if($league['gameType'] == 'PICKSQUARES'){            $message_body.= ($k + 1).'. '.$pick."<br/>";        }else{            $message_body .= ($k + 1).'. '.esc_html($pick['name'])."<br/>";        }    }}?>