<?php $paypal_type = get_option('victorious_paypal_type') != null ? get_option('victorious_paypal_type') : array(); ?><?php VIC_GetMessage(); ?><?php if ($paypal_type != null): ?>    <form id="formAddCredits">        <h3>            <?php echo esc_html(__('Rate'));?>: <?php echo VIC_FormatMoney(1)." ".esc_html(__('deposit equals', 'victorious'));?> <?php echo get_option('victorious_cash_to_credit');?> <?php echo esc_html(__('credits'));?>        </h3>        <?php if (!empty($fee_percentage)): ?>            <h3>                <?php echo esc_html(__('Fee percentage'));?>: <?php echo get_option('victorious_fee_percentage');?>%            </h3>        <?php endif; ?>        <div class="vc-dashboard-item border-white pb-0">            <div class="row">                <div class="col-md-6">                    <h3 class="vc-tabpane-title"><?php echo esc_html(__('How many credits do you want to add', 'victorious'));?> (<?php echo sprintf(esc_html(__('minimum %s')), VIC_FormatMoney(get_option('victorious_minimum_deposit'))); ?>):</h3>                    <input type="text" name="credits" class="form-control" <?php if ($fee_percentage > 0): ?>onkeyup="jQuery.payment.addFundValue(this.value, '<?php echo esc_html($fee_percentage); ?>')"<?php endif; ?> />                    <?php if ($fee_percentage > 0): ?>                        <div class="help-block"><?php echo esc_html(__('Real Value'));?>: <span id="realCredits"></span></div>                    <?php endif; ?>                </div>            </div>        </div>        <?php require_once(VICTORIOUS__PLUGIN_DIR_VIEW . 'Elements/addfund_coupon.php');?>        <?php if (in_array(VICTORIOUS_PAYPAL_TYPE_NORMAL, $paypal_type) && in_array(VICTORIOUS_PAYPAL_TYPE_PRO, $paypal_type)): ?>            <select id="paypalType" class="form-control">                <option value="<?php echo VICTORIOUS_PAYPAL_TYPE_PRO; ?>">                    <?php echo esc_html(__("Credit card", 'victorious'));?>                </option>                <option value="<?php echo VICTORIOUS_PAYPAL_TYPE_NORMAL; ?>">                    <?php echo esc_html(__("Paypal", 'victorious'));?>                </option>            </select>            <div id="paypalPro">                <?php require_once(VICTORIOUS__PLUGIN_DIR_VIEW . 'Elements/paypal_pro.php');?>            </div>            <div id="paypalNormal" style="display: none">                <?php require_once(VICTORIOUS__PLUGIN_DIR_VIEW . 'Elements/paypal_normal.php');?>            </div>        <?php elseif (in_array(VICTORIOUS_PAYPAL_TYPE_PRO, $paypal_type)): ?>            <?php require_once(VICTORIOUS__PLUGIN_DIR_VIEW . 'Elements/paypal_pro.php');?>        <?php elseif (in_array(VICTORIOUS_PAYPAL_TYPE_NORMAL, $paypal_type)): ?>            <?php require_once(VICTORIOUS__PLUGIN_DIR_VIEW . 'Elements/paypal_normal.php');?>        <?php endif; ?>        <input id="btnAddCredits" type="submit" class="vc-button btn-green btn-size-sm btn-radius5 font-weight-normal" value="<?php echo esc_html(__('Add', 'victorious'));?>" onclick="jQuery.payment.sendCredits()" />        <span class="waiting" style="display: none"><?php echo esc_html(__('Please wait...', 'victorious'));?></span>    </form>    <p id="msgAddCredits" class="alert alert-danger public_message" style="display: none"></p><?php else: ?>    <?php echo esc_html(__("There are no available gateways", 'victorious'));?><?php endif; ?><script type="text/javascript">    jQuery(window).load(function () {        initData();    });    jQuery(document).on('change', '#paypalType', function () {        initData();    });    function initData()    {        if (jQuery('#paypalType').val() == <?php echo VICTORIOUS_PAYPAL_TYPE_PRO; ?>)        {            jQuery('#paypalPro').show();            jQuery('#paypalNormal').hide();            jQuery('#paypalPro').find('input').removeAttr('disabled');            jQuery('#paypalPro').find('select').removeAttr('disabled');            jQuery('#paypalNormal').find('input').attr('disabled', 'disabled');            jQuery('#paypalNormal').find('select').attr('disabled', 'disabled');        } else        {            jQuery('#paypalPro').hide();            jQuery('#paypalNormal').show();            jQuery('#paypalNormal').find('input').removeAttr('disabled');            jQuery('#paypalNormal').find('select').removeAttr('disabled');            jQuery('#paypalPro').find('input').attr('disabled', 'disabled');            jQuery('#paypalPro').find('select').attr('disabled', 'disabled');        }    }</script>