<!-- change language --><?php    $languages = get_available_languages();    //$translations = wp_get_available_translations();    if ( ! is_multisite() && defined( 'WPLANG' ) && '' !== WPLANG && 'en_US' !== WPLANG && ! in_array( WPLANG, $languages ) ) {        $languages[] = WPLANG;    }    $locale = get_locale();    if ( ! in_array( $locale, $languages ) ) {        $locale = '';    }    wp_dropdown_languages( array(        'name'         => 'WPLANG',        'id'           => 'WPLANG',        'selected'     => $locale,        'languages'    => $languages,        'translations' => '',        'show_available_translations' => '',    ) );?><script>    jQuery(document).on('change', '#WPLANG', function(){        window.location = "<?php echo strtok($_SERVER["REQUEST_URI"],'?');?>?fvlang=" + jQuery(this).find('option:selected').attr('value');    });</script>