<?php if(!empty($users)):?>    <div class="entry-list">        <?php foreach($users as $k => $user):?>            <div class="entry-list-item">                <div class="entry-list-item-ava">                    <img src="<?php echo esc_url($user['avatar']);?>" alt="">                </div>                <div class="entry-list-item-info">                    <b class="b-text"><?php echo esc_html($user['user_nicename']);?></b>                    <?php if($league['multi_entry']):?>                        <div><?php echo esc_html(__("Entry", "victorious")).' #'.esc_html($user['entry_number']);?></div>                    <?php endif;?>                    <?php if(isset($user['b_icon']) && $user['b_icon'] == 1 && get_option('victorious_b_icon')):?>(B)<?php endif;?>                </div>            </div>        <?php endforeach;?>    </div><?php else:?>    <div class="text-center"><?php echo esc_html(__("There are no entries in this contest yet.", "victorious"));?></div><?php endif;?>