<div class="vc-table-2c">    <div class="vc-table-sm">        <table>            <thead>                <tr>                    <th colspan="2"><b class="b-text"><?php echo esc_html(__("Games", "victorious"));?> </b></th>                </tr>            </thead>            <tbody>                <?php if(!empty($fights)):?>                    <?php foreach($fights as $k => $fight):?>                        <?php if($k == 0 || $k % 2 == 0):?>                        <tr>                        <?php endif;?>                            <td><b class="b-text"><?php echo esc_html(__("A:", "victorious"))." ".$fight['nickName2'];?> @ <?php echo esc_html(__("H:", "victorious"))." ".$fight['nickName1'];?></b><br><?php echo VIC_DateTranslate($fight['startDate']);?></td>                            <td><b class="b-text">PIT @ STL</b><br>Thu 7:00 PM ET</td>                        <?php if($k % 2 == 0 || $k == count($fights) - 1):?>                        </tr>                        <?php endif;?>                    <?php endforeach;?>                <?php endif;?>            </tbody>        </table>    </div></div><?php if($scorings['playerdraft'] != null || $scorings['normal'] != null || $scorings['teamdraft']):?>    <div class="vc-table-2c">        <div class="vc-table-sm">            <table>                <thead>                    <tr>                        <th colspan="2"><b class="b-text"><?php echo esc_html(__("Scoring Categories", "victorious"));?></b></th>                    </tr>                </thead>                <tbody>                    <?php if($scorings['normal'] != null):?>                        <?php foreach($scorings['normal'] as $scoring):?>                            <tr>                                <td><?php echo esc_html($scoring);?></td>                            </tr>                        <?php endforeach;?>                    <?php endif;?>                    <?php if($scorings['playerdraft'] != null && count($scorings['playerdraft']) > 0):?>                        <?php foreach($scorings['playerdraft'] as $scoring):                            $scoring_name = (!empty($scoring['alias']) ? esc_html($scoring['alias']) : str_replace('_', ' ', esc_html($scoring['name'])));                        ?>                            <tr>                                <td><?php echo VIC_ScoringTranslate($scoring_name);?><span class="pull-right b-text"><?php echo esc_html($scoring['points']);?></span></td>                            </tr>                        <?php endforeach;?>                    <?php elseif($scorings['teamdraft'] != null && count($scorings['teamdraft']) > 0):?>                        <?php foreach ($scorings['teamdraft'] as $item):                            $lineup = $item['lineup'];                            $scorings = $item['scorings'];                        ?>                            <tr>                                <td><?php echo esc_html($lineup['name']);?></td>                            </tr>                                <?php foreach ($scorings as $scoring):                                    $scoring_name = (!empty($scoring['alias']) ? esc_html($scoring['alias']) : str_replace('_', ' ', esc_html($scoring['name'])));                                ?>                                    <tr>                                        <td><?php echo VIC_ScoringTranslate($scoring_name);?><span class="pull-right b-text"><?php echo esc_html($scoring['points']);?></span></td>                                    </tr>                                <?php endforeach; ?>                            </ul>                        <?php endforeach; ?>                    <?php elseif($scorings['playerdraft'] != null && $league['is_mixing']):?>                        <?php foreach($scorings['playerdraft'] as $aPlayerDraftScoring):?>                            <tr>                                <td><?php echo esc_html($aPlayerDraftScoring);?></td>                            </tr>                            <?php foreach($aPlayerDraftScorings['name_sport'] as $aScoring):                                $scoring_name = (!empty($scoring['alias']) ? esc_html($scoring['alias']) : str_replace('_', ' ', esc_html($scoring['name'])));                            ?>                                <tr>                                    <td><?php echo VIC_ScoringTranslate($scoring_name);?><span class="pull-right b-text"><?php echo esc_html($scoring['points']);?></span></td>                                </tr>                            <?php endforeach;?>                        <?php endforeach;?>                    <?php endif;?>                </tbody>            </table>            <?php if(!empty($league['bonus'])):?>                <h5 class="f-game-info-scoring-title">Bonus</h5>                <div class="f-game-info-scoring-categories">                    <?php echo esc_html($league['bonus']);?>                </div>            <?php endif;?>            <?php if(!empty($note)):?>                <div class="f-column-12 game-info-scoring" style="margin-top: 10px">                    <h5 class="f-game-info-scoring-title"><?php echo esc_html(__("Note", "victorious"));?></h5>                    <div class="f-game-info-scoring-categories">                        <?php echo esc_html($note);?>                    </div>                </div>            <?php endif;?>        </div>    </div><?php endif;?>