<table class="for_live_draft form-table" style="display: none">    <tr valign="top">        <th scope="row"><?php echo esc_html(__('Number of players that be changed via Waiver Wire', 'victorious'));?> <span class="description">(<?php echo esc_html(__("required", 'victorious'));?>)</span></th>        <td>            <select name="waiver_wire_player_quantity">                <?php for($i = 1; $i <= 9; $i++):?>                    <option value="<?php echo $i;?>" <?php if($aForms['waiver_wire_player_quantity'] == $i):?>selected="selected"<?php endif;?>>                        <?php echo $i;?>                    </option>                <?php endfor;?>            </select>            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>        </td>    </tr>    <tr valign="top">        <th scope="row"><?php echo esc_html(__('Number of minutes a contestant has to draft', 'victorious'));?> <span class="description">(<?php echo esc_html(__("required", 'victorious'));?>)</span></th>        <td>            <input type="text" name="live_draft_minute_change_player" value="<?php echo $aForms['live_draft_minute_change_player'];?>">            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>            <p><?php echo esc_html(__('Must be equal or greater than 2 minutes', 'victorious'));?></p>        </td>    </tr>    <tr valign="top">        <th scope="row">            <?php echo esc_html(__('Start draft date time', 'victorious'));?> <span class="description">(<?php echo esc_html(__("required", 'victorious'));?>)</span>        </th>        <td>            <div class="table">                <div class="table_left" style="width:auto">                    <input type="text" class="form-control disabled" name="live_draft_start_date" id="live_draft_start" value="<?php echo !empty($aForms['live_draft_start']) ? date("m/d/Y", strtotime($aForms['live_draft_start'])) : '';?>" readonly style="width: auto">                </div>                <div class="table_right">                    <?php echo esc_html(__("Hour", 'victorious'));?>                    <select name="live_draft_start_hour">                        <?php for($i = 0; $i <= 23; $i++):?>                        <option value="<?php echo $i;?>" <?php if(!empty($aForms['live_draft_start']) && date("H", strtotime($aForms['live_draft_start'])) == $i):?>selected="selected"<?php endif;?>>                                <?php echo esc_html($i);?>                            </option>                        <?php endfor;?>                    </select>                    <?php echo esc_html(__("Minute", 'victorious'));?>                    <select name="live_draft_start_minute">                        <?php for($i = 0; $i <= 59; $i++):?>                            <option value="<?php echo $i;?>" <?php if(!empty($aForms['live_draft_start']) && date("i", strtotime($aForms['live_draft_start'])) == $i):?>selected="selected"<?php endif;?>>                                <?php echo esc_html($i);?>                            </option>                        <?php endfor;?>                    </select>                </div>            </div>            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>        </td>    </tr>    <tr valign="top">        <th scope="row"><?php echo esc_html(__('Waiver Wire starts', 'victorious'));?> <span class="description">(<?php echo esc_html(__("required", 'victorious'));?>)</span></th>        <td>            <select name="live_draft_waiver_wire_start">                <?php for($i = 0; $i < 7; $i++):                    $day = strtolower(jddayofweek($i,1));                ?>                    <option value="<?php echo $day;?>" <?php if($aForms['live_draft_waiver_wire_start'] == $day):?>selected="selected"<?php endif;?>>                        <?php echo esc_html(ucfirst($day));?>                    </option>                <?php endfor;?>            </select>            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>        </td>    </tr>    <tr valign="top">        <th scope="row"><?php echo esc_html(__('Waiver Wire ends', 'victorious'));?> <span class="description">(<?php echo esc_html(__("required", 'victorious'));?>)</span></th>        <td>            <select name="live_draft_waiver_wire_end">                <?php for($i = 0; $i < 7; $i++):                    $day = strtolower(jddayofweek($i,1));                ?>                    <option value="<?php echo $day;?>" <?php if($aForms['live_draft_waiver_wire_end'] == $day):?>selected="selected"<?php endif;?>>                        <?php echo esc_html(ucfirst($day));?>                    </option>                <?php endfor;?>            </select>            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>        </td>    </tr>    <tr valign="top">        <th scope="row"><?php echo esc_html(__('Number of bench players', 'victorious'));?></th>        <td>            <input type="text" name="live_draft_bench_quantity" value="<?php echo !empty($aForms['live_draft_bench_quantity']) ? $aForms['live_draft_bench_quantity'] : 0;?>">            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>        </td>    </tr>    <tr valign="top">        <th scope="row"><?php echo esc_html(__('Number of minutes users can enter draft room before live draft starts', 'victorious'));?></th>        <td>            <input type="text" name="live_draft_minute_prior_draft_room" value="<?php echo !empty($aForms['live_draft_minute_prior_draft_room']) ? $aForms['live_draft_minute_prior_draft_room'] : 0;?>">            <p><?php echo esc_html(__('Apply to live draft', 'victorious'));?></p>        </td>    </tr></table>