<?php if(!empty($leagues)):?>    <?php foreach($leagues as $league):        $balance_type = $league['balance_type'];    ?>        <?php            $titlewidth = "50%";            if(get_option('victorious_no_cash') == 0){                $titlewidth = "34%";            }        ?>        <tr>            <td data-label="<?php echo esc_html(__('ID', 'victorious'))?>"><?php echo esc_html($league["leagueID"]);?></td>            <td data-label="<?php echo esc_html(__('Date', 'victorious'))?>"><?php echo VIC_DateTranslate($league["startDate"]);?></td>            <td data-label="<?php echo esc_html(__('Name', 'victorious'))?>"><?php echo esc_html($league["name"]);?></td>            <td data-label="<?php echo esc_html(__('Entries', 'victorious'))?>"><?php echo esc_html($league["entries"]);?> / <?php echo esc_html($league["size"]);?></td>            <?php if(get_option('victorious_no_cash') == 0):?>                <td data-label="<?php echo esc_html(__('Entry Fee', 'victorious'))?>"><?php echo esc_html($league['entry_fee'] == 0 ? __("Free", "victorious") : VIC_FormatMoney($league["entry_fee"], $balance_type['currency_code_symbol'], $balance_type['currency_position']));?></td>                <td data-label="<?php echo esc_html(__('Entry Fee', 'victorious'))?>"><?php echo esc_html($league['entry_fee'] == 0 ? __("Free", "victorious") : VIC_FormatMoney($league["entry_fee"], $balance_type['currency_code_symbol'], $balance_type['currency_position']));?></td>                <td data-label="><?php echo esc_html(__('Prizes', 'victorious'))?>"><?php echo VIC_FormatMoney($league["prizes"], $balance_type['currency_code_symbol'], $balance_type['currency_position']);?></td>            <?php endif;?>            <td data-label="><?php echo esc_html(__('Rank', 'victorious'))?>"><?php echo esc_html($league["rank"]);?></td>            <td class="text-right">                <?php if($league['gameType'] == VICTORIOUS_GAME_TYPE_NFL_PLAYOFF && $league['can_change_player']): ?>                    <input style="float:right;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Change Player', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_SUBMIT_PICKS.$league['leagueID']."/?num=".$league['entry_number'];?>'">                <?php endif; ?>                <?php if($league['gameType'] == VICTORIOUS_GAME_TYPE_NFL_PLAYOFF && $league['can_edit']):?>                    <input type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Draft room', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_SUBMIT_PICKS.$league['leagueID']."/?num=".$league['entry_number'];?>'">                <?php endif;?>                <?php if($league["trade_player"] && !$league["trade_pick"]):?>                    <input type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Change', 'victorious'));?>" onclick="window.location = '<?php echo VICTORIOUS_URL_GAME.$league["leagueID"]."?num=".$league["entry_number"];?>'">                <?php endif;?>                <input type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html($league['is_live_draft'] ? __('Weekly Scores', 'victorious') : __('View Results', 'victorious'));?>" onclick="window.location = '<?php echo VICTORIOUS_URL_CONTEST.$league["leagueID"]."/?num=".$league['entry_number'];?>'">                <?php if(($league['show_weekly_pick'] && $league['yearly']) || !empty($league['yearly'])): ?>                <input style="float:right;margin-left: 5px;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Edit', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_GAME.$league['leagueID']."/?num=".$league['entry_number'];?>'">                <?php endif; ?>                <?php if($league['is_live_draft'] && $league['is_have_injury']): ?>                 <input style="float:right;margin-left: 5px;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Change injury players', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_GAME.$league["leagueID"]."?num=".$league["entry_number"].'&action=1';?>'">                <?php endif; ?>                <?php if(isset($league['allow_bench']) && $league['is_live_draft'] && $league['allow_bench'] == 1): ?>                    <input style="float:right;margin-left: 5px;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Manage Lineup', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_GAME."/".$league['leagueID']."?action=4";?>'">                <?php endif;?>                <?php if(isset($league['stats_for_rugby']) && $league['stats_for_rugby'] == 1): ?>                    <input style="float:right;margin-left: 5px;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Stats', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_STATISTIC."/?rugby=1&league_id=".$league['leagueID'];?>'">                <?php endif;?>                <?php if(isset($league['allow_waiver_wire']) && $league['is_live_draft'] && $league['allow_waiver_wire'] == 1): ?>                    <input style="float:right;margin-left: 5px;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Waiver Wire', 'victorious'))?>" onclick="window.location = '<?php echo VICTORIOUS_URL_GAME."/".$league['leagueID']."?action=2";?>'">                <?php endif;?>                <?php if($league['is_live_draft']): ?>                   <input style="float:right;" type="button" class="vc-button btn-yellow btn-size-xs btn-radius5 mb-1" value="<?php echo esc_html(__('Request Trade', 'victorious'))?>" onclick="jQuery.tradeRugby.showListUsers(<?php echo esc_html($league['leagueID']); ?>,<?php echo esc_html($league['entry_number']); ?>)">                <?php endif; ?>            </td>        </tr>    <?php endforeach; ?>    <div id="resultDialog" title="" style="display: none">        <div class="f-loading" style="display: none"></div>    </div><?php endif;?>