<table class="table table-bordered f-condensed table-result">    <thead>        <tr>            <th><?php echo esc_html(__("Fixture", "victorious"));?></th>            <th style="width:25%">                <?php echo esc_html(__("My Pick", "victorious"));?> (<?php echo esc_html($current_user['user_login']);?>)            </th>            <th style="width:25%">                <?php echo esc_html(__("Competitor Pick", "victorious"));?> (<?php echo esc_html($opponent_user['user_login']);?>)            </th>            <th style="width:25%">                <?php echo esc_html(__("Actual Result", "victorious"));?>            </th>        </tr>    </thead>    <?php if($fights != null):?>    <tbody>        <?php foreach($fights as $fight):            $home_team = $fight['home_team'];            $away_team = $fight['away_team'];            $my_pick = $fight['my_pick'];            $opponent_pick = $fight['opponent_pick'];        ?>            <tr>                <td>                    <?php echo esc_html($home_team['name']);?><br/>                    <div><?php echo esc_html(__("VS", "victorious"));?></div>                    <?php echo esc_html($away_team['name']);?><br/>                    <?php echo VIC_DateTranslate($fight['startDate']); ?>                </td>                <td>                    <?php if(!empty($my_pick)):?>                        <div class="<?php echo esc_attr($my_pick['is_correct'] ? "correct" : "incorrect");?>">                            <?php echo esc_html($my_pick['name']);?>                        </div>                        <div>                            <?php echo esc_html(__("Points", "victorious"));?>: <?php echo esc_html($my_pick['points']);?>                        </div>                    <?php endif;?>                </td>                <td>                    <?php if(!empty($opponent_pick)):?>                        <div class="<?php echo esc_attr($opponent_pick['is_correct'] ? "correct" : "incorrect");?>">                            <?php echo esc_html($opponent_pick['name']);?>                        </div>                        <div>                            <?php echo esc_html(__("Points", "victorious"));?>: <?php echo esc_html($opponent_pick['points']);?>                        </div>                    <?php endif;?>                </td>                <td>                    <div class="h_column actual_result">                        <?php if ($fight['is_live']):?>                            <div class="<?php echo esc_attr($fight['winnerID'] == $fight['fighterID1'] ? "correct" : "incorrect");?>">                                <?php echo esc_html($fight['name1']).' '.esc_html($fight['team1score']);?>                            </div>                            <div class="<?php echo esc_attr($fight['winnerID'] == $fight['fighterID2'] ? "correct" : "incorrect");?>">                                <?php echo esc_html($fight['name2']).' '.esc_html($fight['team2score']);?>                            </div>                        <?php endif;?>                    </div>                </td>            </tr>        <?php endforeach;?>        <tr>            <td><?php echo esc_html(__("Total points", "victorious"));?></td>            <td>                <div id="myTotalPoints">                    <?php echo esc_html($total_my_points);?>                </div>            </td>            <td>                <div id="YourTotalPoints">                    <?php echo esc_html($total_opponent_points);?>                </div>            </td>            <td>&nbsp;</td>        </tr>    </tbody>    <?php endif;?></table>