<table class="table table-bordered f-condensed table-result">    <thead>        <tr>            <th><?php echo esc_html(__("Fixture", "victorious"));?></th>            <th style="width:25%">                <?php echo esc_html(__("My Pick", "victorious"));?> (<?php echo esc_html($current_user['user_login']);?>)            </th>            <th style="width:25%">                <?php echo esc_html(__("Competitor Pick", "victorious"));?> (<?php echo esc_html($opponent_user['user_login']);?>)            </th>            <th style="width:25%">                <?php echo esc_html(__("Actual Result", "victorious"));?>            </th>        </tr>    </thead>    <?php if($fights != null):?>    <tbody>        <?php foreach($fights as $fight):            $home_team = $fight['home_team'];            $away_team = $fight['away_team'];            $my_pick = $fight['my_pick'];            $opponent_pick = $fight['opponent_pick'];            $styleTeam1Win = $styleTeam2Win = $styleMyPick = $styleOpponentPick = "";            if($league['is_live'] || $league['is_complete']){                $styleTeam1Win = $styleTeam2Win = $styleMyPick = $styleOpponentPick = "incorrect";                if ($fight['winnerID'] == $fight['fighterID1'] || $fight['winnerID'] == 0)                {                    $styleTeam1Win = "correct";                }                if ($fight['winnerID'] == $fight['fighterID2'] || $fight['winnerID'] == 0)                {                    $styleTeam2Win = "correct";                }                $styleMyPick = $my_pick['is_correct'] ? "correct" : "incorrect";                $styleOpponentPick = $opponent_pick['is_correct'] ? "correct" : "incorrect";            }        ?>            <tr>                <td>                    <div style="font-weight:bold"><?php echo esc_html($home_team['name']);?></div>                    <div><?php echo esc_html(__("vs", "victorious"));?></div>                    <div style="font-weight:bold"><?php echo esc_html($away_team['name']);?></div>                    <?php echo VIC_DateTranslate($fight['startDate']); ?>                </td>                <td>                    <?php if(!empty($my_pick)):?>                        <div class="<?php echo esc_attr($styleMyPick);?>">                            <?php echo esc_html($my_pick['name']);?>                        </div>                        <div>                            <?php echo esc_html(__("Points", "victorious"));?>: <?php echo esc_html($my_pick['points']);?>                        </div>                        <?php if ($my_pick['method'] != ''):?>                            <div><?php echo esc_html(__("Method", "victorious"));?>: <?php echo esc_html($my_pick['method']);?></div>                        <?php endif;?>                        <?php if ($my_pick['round'] != ''):?>                            <div><?php echo esc_html(__("Round", "victorious"));?>: <?php echo esc_html($my_pick['round']);?></div>                        <?php endif;?>                        <?php if ($my_pick['minute'] != ''):?>                            <div><?php echo esc_html(__("Minute", "victorious"));?>: <?php echo esc_html($my_pick['minute']);?></div>                        <?php endif;?>                    <?php endif;?>                </td>                <td>                    <?php if(!empty($opponent_pick)):?>                        <div class="<?php echo esc_attr($styleOpponentPick);?>">                            <?php echo esc_html($opponent_pick['name']);?>                        </div>                        <div>                            <?php echo esc_html(__("Points", "victorious"));?>: <?php echo esc_html($opponent_pick['points']);?>                        </div>                        <?php if ($opponent_pick['method'] != ''):?>                            <div><?php echo esc_html(__("Method", "victorious"));?>: <?php echo esc_html($opponent_pick['method']);?></div>                        <?php endif;?>                        <?php if ($opponent_pick['round'] != ''):?>                            <div><?php echo esc_html(__("Round", "victorious"));?>: <?php echo esc_html($opponent_pick['round']);?></div>                        <?php endif;?>                        <?php if ($opponent_pick['minute'] != ''):?>                            <div><?php echo esc_html(__("Minute", "victorious"));?>: <?php echo esc_html($opponent_pick['minute']);?></div>                        <?php endif;?>                    <?php endif;?>                </td>                <td>                    <div class="h_column actual_result">                        <?php if ($league['is_live'] || $league['is_complete']):?>                            <div class="<?php echo esc_attr($styleTeam1Win);?>">                                <?php echo esc_html($home_team['name']).' '.esc_html($home_team['score']);?>                            </div>                            <div class="<?php echo esc_attr($styleTeam2Win);?>">                                <?php echo esc_html($away_team['name']).' '.esc_html($away_team['score']);?>                            </div>                        <?php endif;?>                    </div>                </td>            </tr>        <?php endforeach;?>        <?php if ($league['allow_new_tie_breaker'] == 1):            $my_selection = $tie_breaker['my_selection'];            $opponent_selection = $tie_breaker['opponent_selection'];            $highest_score_team = $tie_breaker['highest_score_team'];            $player_score = $tie_breaker['player_score'];            $total_goal = $tie_breaker['total_goal'];                        $styleMyCorrectTeam = $styleMyOpponentTeam = '';            $styleMyCorrectPlayer = $styleMyOpponentPlayer = '';            $styleMyCorrectGoal = $styleMyOpponentGoal = '';            if($league['is_live'] || $league['is_complete']){                $styleMyCorrectTeam = $my_selection['correct_team'] ? "correct" : "incorrect";                $styleMyOpponentTeam = $opponent_selection['correct_team'] ? "correct" : "incorrect";                $styleMyCorrectPlayer = $my_selection['correct_player'] ? "correct" : "incorrect";                $styleMyOpponentPlayer = $opponent_selection['correct_player'] ? "correct" : "incorrect";                $styleMyCorrectGoal = $my_selection['correct_goal'] ? "correct" : "incorrect";                $styleMyOpponentGoal = $opponent_selection['correct_goal'] ? "correct" : "incorrect";            }        ?>            <tr>                <td><?php echo esc_html(__("Highest score team prediction", "victorious"));?></td>                <td>                    <div class="<?php echo esc_attr($styleMyCorrectTeam);?>">                        <?php echo esc_html($my_selection['highest_score_team']);?>                    </div>                </td>                <td>                    <div class="<?php echo esc_attr($styleMyOpponentTeam);?>">                        <?php echo esc_html($opponent_selection['highest_score_team']);?>                    </div>                </td>                <td>                    <?php echo esc_html($highest_score_team != null ? implode(', ', $highest_score_team) : "");?>                </td>            </tr>            <tr>                <td><?php echo esc_html(__("Player score prediction", "victorious"));?></td>                <td>                    <div class="<?php echo esc_attr($styleMyCorrectPlayer);?>">                        <?php echo esc_html($my_selection['player_score']);?>                    </div>                </td>                <td>                    <div class="<?php echo esc_attr($styleMyOpponentPlayer);?>">                        <?php echo esc_html($opponent_selection['player_score']);?>                    </div>                </td>                <td>                    <?php echo esc_html($player_score != null ? implode(', ', $player_score) : "");?>                </td>            </tr>            <tr>                <td><?php echo esc_html(__("Total goal prediction", "victorious"));?></td>                <td>                    <div class="<?php echo esc_attr($styleMyCorrectGoal);?>">                        <?php echo esc_html($my_selection['total_goals']);?>                    </div>                </td>                <td>                    <div class="<?php echo esc_attr($styleMyOpponentGoal);?>">                        <?php echo esc_html($opponent_selection['total_goals']);?>                    </div>                </td>                <td>                    <?php echo esc_html($total_goal);?>                </td>            </tr>        <?php endif;?>        <tr>            <td><?php echo esc_html(__("Total points", "victorious"));?></td>            <td>                <div id="myTotalPoints">                    <?php echo esc_html($total_my_points);?>                </div>            </td>            <td>                <div id="YourTotalPoints">                    <?php echo esc_html($total_opponent_points);?>                </div>            </td>            <td>&nbsp;</td>        </tr>    </tbody>    <?php endif;?></table>