<table class="table table-bordered f-condensed table-result">    <thead>        <tr>            <th><?php echo esc_html(__("Fixture", "victorious"));?></th>            <th style="width:25%">                <?php echo esc_html(__("My Pick", "victorious"));?> (<?php echo esc_html($current_user['user_login']);?>)            </th>            <th style="width:25%">                <?php echo esc_html(__("Competitor Pick", "victorious"));?> (<?php echo esc_html($opponent_user['user_login']);?>)            </th>            <th style="width:25%">                <?php echo esc_html(__("Actual Result", "victorious"));?>            </th>        </tr>    </thead>    <?php if($fights != null):        $fight = $fights[0];        $home_team = $fight['home_team'];        $away_team = $fight['away_team'];        $my_pick = $fight['my_pick'];        $opponent_pick = $fight['opponent_pick'];    ?>    <tbody>        <tr>            <td>               <div style="font-weight:bold"><?php echo esc_html($home_team['name']);?></div>                <div><?php echo esc_html(__("vs", "victorious"));?></div>                <div style="font-weight:bold"><?php echo esc_html($away_team['name']);?></div>                <?php echo VIC_DateTranslate($fight['startDate']); ?>            </td>            <td id="myresult_<?php echo esc_attr($fight['fightID']);?>">                <?php echo implode(', ', json_decode($my_pick['pick_squares']));?>            </td>            <td id="yourresult_<?php echo esc_attr($fight['fightID']);?>">                <?php echo implode(', ', json_decode($opponent_pick['pick_squares']));?>            </td>            <td>                <?php if ($fight['is_live'] || $league['is_complete']):?>                <div class="h_column actual_result">                    <div>                        <?php echo (abs($fight['team1score']) % 10);?>_<?php echo (abs($fight['team2score']) % 10);?>                    </div>                </div>                <?php endif;?>            </td>        </tr>    </tbody>    <?php endif;?></table>