<div class="vc-table-small">    <table id="table_standing">        <thead>            <tr>                <th style="width:7%;"><?php echo esc_html(__('Rank', 'victorious'));?></th>                <th><?php echo esc_html(__('User', 'victorious'));?></th>                <?php if ($league['multi_entry'] == 1):?>                    <th style="width:11%;"><?php echo esc_html(__("Entry number", "victorious"));?></th>                <?php endif;?>                <th style="width:14%;"><?php echo esc_html(__('Correct Spread Picks', 'victorious'));?></th>                <th style="width:14%;"><?php echo esc_html(__('Correct Winner Picks', 'victorious'));?></th>                <th style="width:17%;"><?php echo esc_html(__('Correct Over/Under Picks', 'victorious'));?></th>                <th style="width:10%;"><?php echo esc_html(__('Points', 'victorious'));?></th>                <?php if (get_option('victorious_no_cash') == 0): ?>                    <th style="width:14%;"><?php echo esc_html(__('Prizes', 'victorious'));?></th>                <?php endif; ?>            </tr>        </thead>        <tbody>            <?php if($standing != null):?>                <?php foreach($standing as $item):                    $user = $item['user'];                ?>                    <tr id="standing_<?php echo esc_attr($item['userID']);?>_<?php echo esc_attr($item['entry_number']);?>" <?php if($user_id == $item['userID'] && $entry_number == $item['entry_number']):?>class="f-user-highlight"<?php endif;?> data-user_id="<?php echo esc_attr($item['userID']);?>" data-entry_number="<?php echo esc_attr($item['entry_number']);?>">                        <td data-label="<?php echo esc_html(__('Rank', 'victorious'));?>">                            <?php echo VIC_ParseRank($item['rank']);?>                        </td>                        <td data-label="<?php echo esc_html(__('User', 'victorious'));?>">                            <b><?php echo esc_html($user['username']);?></b>                        </td>                        <?php if ($league['multi_entry'] == 1):?>                            <td data-label="<?php echo esc_html(__("Entry number", "victorious"));?>"><?php echo esc_html($item['entry_number']);?></td>                        <?php endif;?>                        <td data-label="<?php echo esc_html(__('Correct Spread Picks', 'victorious'));?>">                            <?php echo esc_html($item['winner_spread']);?>                        </td>                        <td data-label="<?php echo esc_html(__('Correct Winner Picks', 'victorious'));?>">                            <?php echo esc_html($item['winners']);?>                        </td>                        <td data-label="<?php echo esc_html(__('Correct Over/Under Picks', 'victorious'));?>">                            <?php echo esc_html($item['winner_over_under']);?>                        </td>                        <td data-label="<?php echo esc_html(__('Points', 'victorious'));?>">                            <?php echo esc_html($item['points']);?>                        </td>                        <?php if (get_option('victorious_no_cash') == 0): ?>                            <td data-label="<?php echo esc_html(__('Prizes', 'victorious'));?>">                                <?php if(!empty($item['prize'])):                                    $prize = $item['prize'];                                ?>                                    <?php echo esc_html($prize['amount']).' - '.esc_html(strtolower($prize['type']));?>                                <?php endif;?>                                &nbsp;                            </td>                        <?php endif;?>                    </tr>                <?php endforeach;?>            <?php endif; ?>        </tbody>    </table>    <div id="paging_ranking" class="pg-wrapper">        <?php if ($total_page > 1):?>            <?php for ($page = 1; $page <= $total_page; $page++):                $class_active = '';                if ($current_page == $page)                {                    $class_active = 'active';                }            ?>                <div class="dib <?php echo esc_attr($class_active);?>" onclick="jQuery.pickem.loadResult('<?php echo esc_attr($page);?>')">                    <?php echo esc_html($page);?>                </div>            <?php endfor;?>        <?php endif;?>    </div></div>