<table id="table_standing">    <thead>        <tr>            <th style="width:5%;"><?php echo esc_html(__('Rank', 'victorious'));?></th>            <th><?php echo esc_html(__('User', 'victorious'));?></th>            <?php if ($league['multi_entry'] == 1): ?>                <th style="width:10%;"><?php echo esc_html(__('Entry', 'victorious'));?></th>            <?php endif; ?>            <th style="width:10%;"><?php echo esc_attr($league['gameType'] == 'GOLFSKIN' ? __('Skin', 'victorious') : __('Score', 'victorious'));?></th>            <?php if (get_option('victorious_no_cash') == 0): ?>                <th style="width:10%;"><?php echo esc_html(__('Prizes', 'victorious'));?></th>            <?php endif; ?>        </tr>    </thead>    <tbody>        <?php if($scores != null):?>            <?php foreach($scores as $score):?>                <tr <?php if(!empty($score['current'])):?>class="f-user-highlight"<?php endif;?> href="javascript:void(0)" data-user_id="<?php echo esc_attr($score['userID']);?>" data-entry_number="<?php echo esc_attr($score['entry_number']);?>" >                    <td data-label="<?php echo esc_html(__('Rank', 'victorious'));?>">                        <?php echo VIC_ParseRank($score['rank']);?>                    </td>                    <td data-label="<?php echo esc_html(__('User', 'victorious'));?>">                        <b><?php echo esc_html($score['username']);?></b>                    </td>                    <?php if ($league['multi_entry'] == 1): ?>                        <td data-label="<?php echo esc_html(__('Entry', 'victorious'));?>">                            <?php echo esc_html($score['entry_number']);?>                        </td>                    <?php endif;?>                    <td data-label="<?php echo esc_attr($league['gameType'] == 'GOLFSKIN' ? __('Skin', 'victorious') : __('Score', 'victorious'));?>">                        <?php echo esc_html($score['points']);?>                    </td>                    <?php if($no_cash == 0):?>                        <td data-label="<?php echo esc_html(__('Prizes', 'victorious'));?>">                            <?php echo esc_html($score['amount']);?>                        </td>                    <?php endif;?>                </tr>            <?php endforeach;?>        <?php endif; ?>    </tbody></table>