<table class="f-condensed" id="table_standing">    <thead>    <tr>        <th style="width:5%;"></th>        <th><?php echo esc_html(__('User', 'victorious'));?></th>        <?php if ($league['multi_entry'] == 1): ?>            <th style="width:10%;"><?php echo esc_html(__('Entry', 'victorious'));?></th>        <?php endif; ?>        <th style="width:10%;"><?php echo esc_html(__('Score', 'victorious'));?></th>        <?php if (get_option('victorious_no_cash') == 0): ?>            <th style="width:10%;"><?php echo esc_html(__('Prizes', 'victorious'));?></th>        <?php endif; ?>    </tr>    </thead>    <tbody>    <?php if ($standing != null): ?>        <?php foreach ($standing as $k => $item):            $user = $item['user'];            ?>            <tr class="<?php echo isset($active_row) && $active_row == $k ? 'f-user-highlight' : '';?>" id="<?php echo esc_attr($item['id']);?>" data-id="<?php echo esc_attr($item['id']);?>" data-user_id="<?php echo esc_attr($item['userID']);?>" data-entry_number="<?php echo esc_attr($item['entry_number']);?>">                <td data-label="<?php echo esc_html(__('Rank', 'victorious'));?>">                    <?php echo esc_html($item['rank']);?>                </td>                <td data-label="<?php echo esc_html(__('User', 'victorious'));?>">                    <b><?php echo esc_html($user['username']);?></b>                    <?php if($item['playoff_eliminated']):?>                        (<?php echo esc_html(__('Eliminated', 'victorious'));?>)                    <?php endif;?>                </td>                <?php if ($league['multi_entry'] == 1):?>                    <td data-label="<?php echo esc_html(__('Entry', 'victorious'));?>"><?php echo esc_html($item['entry_number']);?></td>                <?php endif;?>                <td data-label="<?php echo esc_html(__('Score', 'victorious'));?>">                    <?php echo esc_html($item['points']); ?>                </td>                <?php if (get_option('victorious_no_cash') == 0): ?>                    <td data-label="<?php echo esc_html(__('Prizes', 'victorious'));?>">                        <?php if(!empty($item['prize'])):                            $prize = $item['prize'];                            ?>                            <?php echo VIC_FormatMoney($prize['amount']).' - '.esc_html(strtolower($prize['type']));?>                        <?php endif;?>                    </td>                <?php endif; ?>            </tr>        <?php endforeach; ?>    <?php endif; ?>    </tbody></table><div id="paging_ranking" class="pg-wrapper">    <?php if ($total_page > 1): ?>        <?php for ($page = 1; $page <= $total_page; $page++):            $class_active = '';            if ($current_page == $page)            {                $class_active = 'active';            }            ?>            <div class="dib <?php echo esc_attr($class_active); ?>" onclick="jQuery.olddraft.loadResult('<?php echo esc_attr($page); ?>')">                <?php echo esc_html($page); ?>            </div>        <?php endfor; ?>    <?php endif; ?></div>