<?php    $check_won = false;    if(!empty($standing) && $league['is_complete']){        foreach($standing as $item){            if($item['userID'] == VIC_GetUserId() && $item['entry_number'] == $league['entry_number'] && !empty($item['prize'])){                $check_won = true;            }        }    }?><div class="vc-header">    <div class="vc-header-left">        <h3 class="vc-title text-uppercase"><?php echo esc_html($league['name']); ?></h3>        <div class="d-inline-flex flex-column mr-4"><b><?php echo esc_html($league['entries']); ?> / <?php echo esc_html($league['size']); ?></b><span><?php echo esc_html(__('Entries', 'victorious')) ?></span></div>        <div class="d-inline-flex flex-column mr-4"><b><?php echo VIC_FormatMoney($league['entry_fee'], $balance_type['currency_code_symbol'], $balance_type['currency_position']); ?></b><span><?php echo esc_html(__('Entry fee', 'victorious')) ?></span></div>        <?php if($league['gameType'] == VICTORIOUS_GAME_TYPE_PORTFOLIO || $league['gameType'] == VICTORIOUS_GAME_TYPE_OLDDRAFT):?>            <div class="d-inline-flex flex-column mr-4"><b><?php echo VIC_DateTranslate($league['contest_cut_date']); ?></b><span><?php echo esc_html(__("Contest starts", 'victorious'));?></span></div>            <div class="d-inline-flex flex-column mr-4"><b><?php echo VIC_DateTranslate($league['contest_end_date']); ?></b><span><?php echo esc_html(__("Contest ends", 'victorious'));?></span></div>        <?php else:?>            <div class="d-inline-flex flex-column mr-4"><b><?php echo VIC_DateTranslate($league['startDate']); ?></b><span><?php echo esc_html(__("Contest starts", 'victorious'));?></span></div>        <?php endif;?>        <?php if($league['gameType'] == VICTORIOUS_GAME_TYPE_OLDDRAFT):?>            <div class="d-inline-flex flex-column mr-4"><b><?php echo esc_html($league['olddraft_year']); ?></b><span><?php echo esc_html(__("Year", 'victorious'));?></span></div>            <div class="d-inline-flex flex-column mr-4"><b><?php echo esc_html($league['olddraft_game']); ?></b><span><?php echo esc_html(__("Game", 'victorious'));?></span></div>        <?php endif;?>        <div class="d-inline-flex flex-column">            <?php if($check_won):?>                <a href="javascript:void(0)" class="vc-button btn-green btn-size-sm btn-radius5"><b><?php echo esc_html(__('Prizes', 'victorious').': '.VIC_FormatMoney($league['prizes'], $balance_type['currency_code_symbol'], $balance_type['currency_position'])); ?></b></a>            <?php else:?>                <b><?php echo VIC_FormatMoney($league['prizes'], $balance_type['currency_code_symbol'], $balance_type['currency_position']); ?></b><span><?php echo esc_html(__('Prizes', 'victorious'));?></span>            <?php endif;?>        </div>    </div>    <div class="vc-header-right">        <?php if($league['gameType'] != VICTORIOUS_GAME_TYPE_PLAYERDRAFT && $league['gameType'] != VICTORIOUS_GAME_TYPE_NFL_PLAYOFF):?>            <?php if(!$league['is_complete'] && !$league['is_live']):?>                <a href="<?php echo VICTORIOUS_URL_SUBMIT_PICKS.$league['leagueID']."/?num=".$league['entry_number'];?>" class="vc-button btn-green btn-size-sm btn-radius5"><b><?php echo esc_html(__('Edit', 'victorious'));?></b></a>            <?php endif;?>            <a href="javascript:void(0)" class="vc-button btn-green btn-size-sm btn-radius5" onclick="return jQuery.global.ruleScoring(<?php echo esc_html($league['leagueID']); ?>, 4)"><b><?php echo esc_html(__('Share', 'victorious'));?></b></a>        <?php endif;?>        <p><a href="javascript:void(0)" class="color-blue" onclick="return jQuery.global.ruleScoring(<?php echo esc_html($league['leagueID']); ?>)"><?php echo esc_html(__('Rules &amp; Scoring', 'victorious'));?></a></p>    </div></div><?php if(!empty($_GET['pick'])):?>    <div id="successSubmitModal" data-backdrop="static" class="modal fade" role="dialog">        <div class="modal-dialog modal-xl modal-dialog-scrollable">            <div class="modal-content">                <div class="modal-header">                    <button type="button" class="close" data-dismiss="modal" aria-label="Close"><span aria-hidden="true">&times;</span></button>                </div>                <div class="modal-body">                    <div>                        <?php echo esc_html(__('Your picks are sumitted! This page will update in automatically. Once the event starts you will be able to click on a player in the contest', 'victorious'));?>                    </div>                    <label>                        <input type="checkbox" id="pick-popup-hide">                        <?php echo esc_html(__('Don\'t show this message again', 'victorious'));?>                    </label>                </div>            </div>        </div>    </div>    <script type="text/javascript">        jQuery(document).ready(function(){            jQuery.global.initSuccessSubmitModal();        })    </script><?php endif;?>