<?php if($predict_match != null):?><div class="contestStructure">    <div class="left">        <div>            <div class="label"><?php echo esc_html(__('Predict for match', 'victorious'));?>:</div>            <?php echo esc_html($predict_match['name']);?>        </div>        <div>            <div class="label"><?php echo esc_html(__('Total predict match score', 'victorious'));?>:</div>            <?php echo esc_html($total_predict_match_score);?>        </div>    </div></div><div class="clear"></div><?php endif;?><div class="f-column-12 f-small-screen-pane score_board" id="f-live-scoring-leaderboard">    <div id="wrapper_standing">        <table class="f-condensed" id="table_standing">            <thead>                <tr>                    <th style="width:7%;"><?php echo esc_html(__('Rank', 'victorious'));?></th>                    <th class="f-text-align-left"><?php echo esc_html(__('User', 'victorious'));?></th>                    <?php if($week > 0):?>                    <th style="width:15%;"><?php echo esc_html(__('Tiebreaker', 'victorious'));?></th>                    <?php endif;?>                    <th style="width:10%;"><?php echo esc_html(__('Points', 'victorious'));?></th>                    <?php if (get_option('victorious_no_cash') == 0): ?>                        <th style="width:14%;"><?php echo esc_html(__('Prizes', 'victorious'));?></th>                    <?php endif; ?>                </tr>            </thead>            <tbody>                <?php if($standing != null):?>                    <?php foreach($standing as $item):                        $user = $item['user'];                    ?>                        <tr <?php if($user_id == $item['userID'] && $entry_number == $item['entry_number']):?>class="f-user-highlight"<?php endif;?> data-user_id="<?php echo esc_attr($item['userID']);?>" data-entry_number="<?php echo esc_attr($item['entry_number']);?>">                            <td class="f-text-align-center">                                <?php echo !empty($item['rank']) ? esc_html($item['rank']) : "-";?>                            </td>                            <td>                                <div style="background-image: url('<?php echo esc_url($user['avatar']);?>')" class="f-avatar">                                </div>                                <a class="f-truncate">                                    <?php echo esc_html($user['username']);?>                                </a>                            </td>                            <?php if($week > 0):?>                            <td class="f-text-align-center">                                <?php echo esc_html($item['predict_point']);?>                            </td>                            <?php endif;?>                            <td class="f-text-align-center">                                <?php echo esc_html($item['points']);?>                            </td>                            <?php if (get_option('victorious_no_cash') == 0): ?>                                <td class="f-text-align-center">                                    <?php if(!empty($item['prize'])):                                        $prize = $item['prize'];                                    ?>                                        <?php echo esc_html($prize['amount']).' - '.esc_html(strtolower($prize['type']));?>                                    <?php endif;?>                                </td>                            <?php endif;?>                        </tr>                    <?php endforeach;?>                <?php endif; ?>            </tbody>        </table>                <div id="paging_ranking" class="pg-wrapper">            <?php if ($total_page > 1):?>                <?php for ($page = 1; $page <= $total_page; $page++):                    $class_active = '';                    if ($current_page == $page)                    {                        $class_active = 'active';                    }                ?>                    <div class="dib <?php echo esc_attr($class_active);?>" onclick="jQuery.roundpickem.loadResult('<?php echo esc_attr($league_id);?>', '<?php echo esc_attr($entry_number);?>', '<?php echo esc_attr($page);?>')">                        <?php echo esc_html($page);?>                    </div>                <?php endfor;?>            <?php endif;?>        </div>    </div></div>