<div class="vc-table-small">    <table id="table_standing">        <thead>        <tr>            <th style="width:7%;"><?php echo esc_html(__('Rank', 'victorious'));?></th>            <th><?php echo esc_html(__('User', 'victorious'));?></th>            <th style="width:10%;"><?php echo esc_html(__('Points', 'victorious'));?></th>            <?php if (get_option('victorious_no_cash') == 0): ?>                <th style="width:14%;"><?php echo esc_html(__('Prizes', 'victorious'));?></th>            <?php endif; ?>        </tr>        </thead>        <tbody>        <?php if($standing != null):?>            <?php foreach($standing as $item):                $user = $item['user'];                ?>                <tr id="standing_<?php echo esc_attr($item['userID']);?>_<?php echo esc_attr($item['entry_number']);?>" <?php if($user_id == $item['userID'] && $entry_number == $item['entry_number']):?>class="f-user-highlight"<?php endif;?> data-user_id="<?php echo esc_attr($item['userID']);?>" data-entry_number="<?php esc_attr(item['entry_number']);?>">                    <td data-label="<?php echo esc_html(__('Rank', 'victorious'));?>">                        <?php echo VIC_ParseRank($item['rank']);?>                    </td>                    <td data-label="<?php echo esc_html(__('User', 'victorious'));?>">                        <b><?php echo esc_html($user['username']);?></b>                    </td>                    <td data-label="<?php echo esc_html(__('Points', 'victorious'));?>">                        <?php echo esc_html($item['points']);?>                    </td>                    <?php if (get_option('victorious_no_cash') == 0): ?>                        <td data-label="<?php echo esc_html(__('Prizes', 'victorious'));?>">                            <?php if(!empty($item['prize'])):                                $prize = $item['prize'];                                ?>                                <?php echo esc_html($prize['amount']).' - '.esc_html(strtolower($prize['type']));?>                            <?php endif;?>                            &nbsp;                        </td>                    <?php endif;?>                </tr>            <?php endforeach;?>        <?php endif; ?>        </tbody>    </table>    <div id="paging_ranking" class="pg-wrapper">        <?php if ($total_page > 1):?>            <?php for ($page = 1; $page <= $total_page; $page++):                $class_active = '';                if ($current_page == $page)                {                    $class_active = 'active';                }                ?>                <div class="dib <?php echo esc_attr($class_active);?>" onclick="jQuery.pickem.loadResult('<?php echo esc_attr($page);?>')">                    <?php echo esc_html($page);?>                </div>            <?php endfor;?>        <?php endif;?>    </div></div>