<div class="p-4 bg-gray">    <div class="vc-table">        <table>            <thead>                <tr>                    <th style="width:10%"><?php echo esc_html(__('ID', 'victorious'));?></th>                    <th style="width:10%"><?php echo esc_html(__('DATE Placed', 'victorious'));?></th>                    <th><?php echo esc_html(__('Description', 'victorious'));?></th>                    <th style="width:20%"><?php echo esc_html(__('Wage/win', 'victorious'));?></th>                </tr>            </thead>            <tbody>                <?php if($picks != null):?>                    <?php foreach($picks as $betslip => $pick_list):                        $parlay = array();                        if($pick_list != null)                        {                            foreach($pick_list as $k => $pick)                            {                                if($pick['wager_type'] != 'parlay')                                {                                    continue;                                }                                $parlay = $pick;                                unset($pick_list[$k]);                                break;                            }                        }                        ?>                        <?php if($parlay):?>                        <tr>                            <td><?php echo esc_html(__('Parlay', 'victorious'));?></td>                            <td><?php echo date('M d Y', strtotime($parlay['created']));?></td>                            <td>                            </td>                            <td>                                <div><?php echo esc_html($parlay['wager']);?>/<?php echo esc_html($parlay['to_win']);?></div>                            </td>                        </tr>                    <?php endif;?>                        <tr>                            <td><?php echo esc_html(__('Betslip', 'victorious')).' '.esc_html($betslip); ?></td>                            <td><?php echo date('M d Y', strtotime($pick_list[0]['created']));?></td>                            <td>                                <div><?php echo esc_html(__('STRAIGHT BET', 'victorious'));?></div>                                <?php foreach($pick_list as $pick):                                    $fight = isset($fights[$pick['fight_id']]) ? $fights[$pick['fight_id']] : array();                                    $pick_value = '';                                    $wager_type = '';                                    $point_price = 0;                                    $point_value = 0;                                    switch ($pick['wager_type']){                                        case 'win':                                            $wager_type = esc_html(__("WIN BETTING", "victorious"));                                            if($pick['team_id'] == $fight['home_team']['teamID']){                                                $point_price = $fight['team1_win'];                                                $pick_value = $fight['home_team']['name'];                                            }                                            else if($pick['team_id'] == $fight['away_team']['teamID']){                                                $point_price = $fight['team2_win'];                                                $pick_value = $fight['away_team']['name'];                                            }                                            break;                                        case 'over':                                            $wager_type = esc_html(__("TOTAL POINTS SCORED", "victorious"));                                            $point_price = $fight['total_over'];                                            $pick_value = esc_html(__("Over", "victorious")).' '.$fight['total_over_under'];                                            break;                                        case 'under':                                            $wager_type = esc_html(__("TOTAL POINTS SCORED", "victorious"));                                            $point_price = $fight['total_under'];                                            $pick_value = esc_html(__("Under", "victorious")).' '.$fight['total_over_under'];                                            break;                                        case 'draw':                                            $wager_type = esc_html(__("DRAW BETTING", "victorious"));                                            $point_price = $fight['team_draw'];                                            $pick_value = esc_html(__("Draw", "victorious"));                                            break;                                        case 'spread':                                            $wager_type = esc_html(__("SPREAD BETTING", "victorious"));                                            if($pick['team_id'] == $fight['home_team']['teamID']){                                                $point_price = $fight['team1_win'];                                                $pick_value = $fight['home_team']['name'];                                            }                                            else if($pick['team_id'] == $fight['away_team']['teamID']){                                                $point_price = $fight['team2_win'];                                                $pick_value = $fight['away_team']['name'];                                            }                                            break;                                    }                                    //check correct                                    $stylePick = "";                                    if($fight['started']){                                        if ($pick['points'] > 0)                                        {                                            $stylePick = "correct-team";                                        }                                        else                                        {                                            $stylePick = "wrong-team";                                        }                                    }                                    ?>                                    <div class="<?php echo esc_attr($stylePick);?>">- <?php echo esc_html($pick_value);?> - <?php echo esc_html($wager_type);?> (<?php echo esc_html($point_price);?>) [<?php echo esc_html($fight['home_team']['name']).' vs '.esc_html($fight['away_team']['name']);?>]</div>                                <?php endforeach;?>                            </td>                            <td>                                <?php foreach($pick_list as $pick):?>                                    <div><?php echo esc_html($pick['wager']);?>/<?php echo esc_html($pick['to_win']);?></div>                                <?php endforeach;?>                            </td>                        </tr>                    <?php endforeach;?>                <?php endif;?>            </tbody>        </table>    </div></div>