<?php    $sport = $data['sport'];    $playerstats = $data['playerstats'];    $scoring_categories = $data['scoring_categories'];    $total_page = $data['total_page'];    $page = $data['page'];    $sort_name = $data['sort_name'];    $sort_value = $data['sort_value'];?><div class="f-loading" style="display: none"></div><div id="wrap_outerdiv">    <?php if($playerstats != null):?>        <div class="outerdiv" style="<?php if(!$sport['no_position'] && $sport['is_team']):?>right: 394px;<?php else:?>right: 202px;<?php endif;?>">            <div id="tbl" class="tbl-wrapper innerdiv" style="display: block; <?php if(!$sport['no_position'] && $sport['is_team']):?>margin-left: 394px;<?php else:?>margin-left:200px<?php endif;?>">                <table border="1">                    <thead>                        <tr>                            <th data-sort_name="player_name" data-sort_value="<?php echo esc_html($sort_name == 'player_name' ? $sort_value : '');?>" style="width:200px" class="frozen" title="<?php echo esc_html(__("Name", 'victorious'));?>">                                <?php echo esc_html(__("Name", 'victorious'));?>                                <?php if($sort_name == 'player_name'):?>                                    <i class="fa fa-sort-up" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'asc' ? 'display: none' : '');?>"></i>                                    <i class="fa fa-sort-down" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'desc' ? 'display: none' : '');?>"></i>                                <?php endif;?>                            </th>                            <?php if(!$sport['no_position']):?>                            <th data-sort_name="position" data-sort_value="<?php echo esc_html($sort_name == 'position' ? $sort_value : '');?>" title="<?php echo esc_html(__("Position", 'victorious'));?>" class="frozen col_position" style="width:97px">                                <?php echo esc_html(__("Position", 'victorious'));?>                                <?php if($sort_name == 'position'):?>                                    <i class="fa fa-sort-up" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'asc' ? 'display: none' : '');?>"></i>                                    <i class="fa fa-sort-down" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'desc' ? 'display: none' : '');?>"></i>                                <?php endif;?>                            </th>                            <?php endif;?>                            <?php if($sport['is_team']):?>                            <th data-sort_name="team" data-sort_value="<?php echo esc_html($sort_name == 'team' ? $sort_value : '');?>" style="width:97px" class="frozen col_team" title="<?php echo esc_html(__("Team", 'victorious'));?>">                                <?php echo esc_html(__("Team", 'victorious'));?>                                <?php if($sort_name == 'team'):?>                                    <i class="fa fa-sort-up" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'asc' ? 'display: none' : '');?>"></i>                                    <i class="fa fa-sort-down" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'desc' ? 'display: none' : '');?>"></i>                                <?php endif;?>                            </th>                            <?php endif;?>                            <?php if($scoring_categories != null):?>                                <?php foreach($scoring_categories as $scoring_category):?>                                <th title="<?php echo esc_html($scoring_category['name']);?>" data-sort_name="<?php echo esc_html($scoring_category['sort_key']);?>" data-sort_value="<?php echo esc_html($sort_name == $scoring_category['sort_key'] ? $sort_value : '');?>">                                    <?php echo esc_html($scoring_category['alias']);?>                                    <?php if($sort_name == $scoring_category['sort_key']):?>                                        <i class="fa fa-sort-up" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'asc' ? 'display: none' : '');?>"></i>                                        <i class="fa fa-sort-down" style="margin-left:5px;<?php echo esc_html($sort_value == '' || $sort_value == 'desc' ? 'display: none' : '');?>"></i>                                    <?php endif;?>                                </th>                                <?php endforeach;?>                            <?php endif;?>                        </tr>                    </thead>                    <tbody>                        <?php if($playerstats != null):?>                            <?php foreach($playerstats as $k => $playerstat):                                $player = $playerstat['player'];                                $position = !empty($player['position']) ? $player['position'] : array();                                $team = !empty($player['team']) ? $player['team'] : array();                                $stats = $playerstat['stats'];                            ?>                                <tr class="<?php if($k %2 == 0):?>tr0<?php else:?>tr1<?php endif;?>">                                    <td style="width:200px" class="frozen">                                        <?php echo esc_html($player['name']);?>                                    </td>                                    <?php if($position != null):?>                                    <td style="width:97px" class="frozen col_position">                                        <?php echo esc_html($position['name']);?>                                    </td>                                    <?php endif;?>                                    <?php if($team != null):?>                                    <td style="width:97px" class="frozen col_team" title="<?php echo esc_html($team['name']);?>">                                        <?php echo esc_html($team['nickName']);?>                                    </td>                                    <?php endif;?>                                    <?php if($stats != null):?>                                        <?php foreach($stats as $stat):?>                                            <td>                                                <?php echo esc_html($stat['points']);?>                                            </td>                                        <?php endforeach;?>                                    <?php endif;?>                                </tr>                            <?php endforeach;?>                        <?php endif;?>                    </tbody>                </table>            </div>        </div>    <?php else:?>        <div class="no-data"><?php echo esc_html(__("No data", 'victorious'));?></div>    <?php endif;?></div><?php if($playerstats != null && $total_page > 1):?><div id="stats-page" class="pg-wrapper">    <?php for($i = 1; $i <= $total_page; $i++):?>        <div class="page-item <?php if($page == $i):?>active<?php endif;?>" data-page="<?php echo esc_html($i);?>">            <?php echo esc_html($i);?>        </div>    <?php endfor;?></div><?php endif;?>