<div class="f-loading" style="display: none"></div><div id="wrap_outerdiv">    <?php if($playerstats != null):?>        <table id="stats-table">            <thead>                <tr>                    <th data-sort_name="position" data-sort_value="<?php echo $sort_name == 'position' ? $sort_value : '';?>" title="<?php echo esc_html(__("Position", 'victorious'));?>" class="data_filter" style="width:15%">                        <?php echo esc_html(__("Position", 'victorious'));?>                        <?php if($sort_name == 'position'):?>                            <i class="fa fa-sort-up" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'asc' ? 'display: none' : '';?>"></i>                            <i class="fa fa-sort-down" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'desc' ? 'display: none' : '';?>"></i>                        <?php endif;?>                    </th>                    <th data-sort_name="player_name" data-sort_value="<?php echo $sort_name == 'player_name' ? $sort_value : '';?>" class="data_filter" title="<?php echo esc_html(__("Name", 'victorious'));?>">                        <?php echo esc_html(__("Name", 'victorious'));?>                        <?php if($sort_name == 'player_name'):?>                            <i class="fa fa-sort-up" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'asc' ? 'display: none' : '';?>"></i>                            <i class="fa fa-sort-down" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'desc' ? 'display: none' : '';?>"></i>                        <?php endif;?>                    </th>                    <th data-sort_name="team" data-sort_value="<?php echo $sort_name == 'team' ? $sort_value : '';?>" style="width:15%" class="data_filter" title="<?php echo esc_html(__("Team", 'victorious'));?>">                        <?php echo esc_html(__("Team", 'victorious'));?>                        <?php if($sort_name == 'team'):?>                            <i class="fa fa-sort-up" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'asc' ? 'display: none' : '';?>"></i>                            <i class="fa fa-sort-down" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'desc' ? 'display: none' : '';?>"></i>                        <?php endif;?>                    </th>                    <th data-sort_name="owner" data-sort_value="<?php echo $sort_name == 'owner' ? $sort_value : '';?>" title="<?php echo esc_html(__("Owner", 'victorious'));?>" class="data_filter" style="width:15%">                        <?php echo esc_html(__("Owner", 'victorious'));?>                        <?php if($sort_name == 'owner'):?>                            <i class="fa fa-sort-up" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'asc' ? 'display: none' : '';?>"></i>                            <i class="fa fa-sort-down" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'desc' ? 'display: none' : '';?>"></i>                        <?php endif;?>                    </th>                    <th title="<?php echo esc_html(__("Opposition", 'victorious'));?>" style="width:15%;cursor: default">                        <?php echo esc_html(__("Opposition", 'victorious'));?>                    </th>                    <th data-sort_name="<?php echo !empty($params['scoring_category_id']) ? $params['scoring_category_id'] : 'value';?>" data-sort_value="<?php echo $sort_name == 'value' || $sort_name == $params['scoring_category_id'] ? $sort_value : '';?>" title="<?php echo esc_html(__("Value", 'victorious'));?>" class="data_filter" style="width:10%">                        <?php echo esc_html(__("Value", 'victorious'));?>                        <?php if($sort_name == 'value'|| $sort_name == $params['scoring_category_id']):?>                            <i class="fa fa-sort-up" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'asc' ? 'display: none' : '';?>"></i>                            <i class="fa fa-sort-down" style="margin-left:5px;<?php echo $sort_value == '' || $sort_value == 'desc' ? 'display: none' : '';?>"></i>                        <?php endif;?>                    </th>                </tr>            </thead>            <tbody>                <?php if($playerstats != null):?>                    <?php foreach($playerstats as $k => $playerstat):?>                        <tr class="<?php if($k %2 == 0):?>tr0<?php else:?>tr1<?php endif;?>">                            <td>                                <?php echo esc_html($playerstat['position_name']);?>                            </td>                            <td>                                <a onclick="jQuery.stats.playerInfo(<?php echo $playerstat['player_id'];?>)" href="javascript:void(0)">                                    <?php echo esc_html($playerstat['name']);?>                                </a>                            </td>                            <td title="<?php echo $playerstat['team'];?>">                                <?php echo esc_html($playerstat['team_alias']);?>                            </td>                            <td>                                <?php echo $playerstat['owner'] != null ? $playerstat['owner'] : esc_html(__("Free agent", 'victorious'));?>                            </td>                            <td title="<?php echo $playerstat['team_opponent'];?>">                                <?php echo esc_html($playerstat['team_opponent_alias']);?>                            </td>                            <td>                                <?php                                     if($params['scoring_category_id'] > 0)                                    {                                        foreach($playerstat['scoring'] as $scoring)                                        {                                            if($scoring['scoring_category_id'] == $params['scoring_category_id'])                                            {                                                echo esc_html($scoring['points']);                                                break;                                            }                                        }                                    }                                    else                                    {                                        echo esc_html($playerstat['total_points']);                                    }                                ;?>                            </td>                        </tr>                    <?php endforeach;?>                <?php endif;?>            </tbody>        </table>    <?php else:?>        <div class="no-data"><?php echo esc_html(__("No data", 'victorious'));?></div>    <?php endif;?></div><?php if($playerstats != null && $total_page > 1):?><div id="stats-page" class="pg-wrapper">    <?php for($i = 1; $i <= $total_page; $i++):?>        <div class="page-item <?php if($page == $i):?>active<?php endif;?>" data-page="<?php echo $i;?>">            <?php echo $i;?>        </div>    <?php endfor;?></div><?php endif;?>