<table class="<?php if(!empty($admin_page)):?>wp-list-table widefat fixed striped books<?php else:?>table table-bordered f-condensed table-result<?php endif;?>">    <thead>        <tr>            <th><?php echo esc_html(__("Fixture", "victorious"));?></th>            <th style="width:20%">                <?php echo esc_html(__("Total Kill", "victorious"));?>            </th>            <th style="width:20%">                <?php echo esc_html(__("Finish", "victorious"));?>            </th>            <th style="width:20%">                <?php echo esc_html(__("Total Point", "victorious"));?>            </th>            <th style="width:20%">                <?php echo esc_html(__("Photo", "victorious"));?>            </th>        </tr>    </thead>    <?php if($fights != null):?>    <tbody>        <?php foreach($fights as $fight):            $my_pick = $fight['my_pick'];        ?>            <tr>                <td>                    <div style="font-weight:bold"><?php echo esc_html($fight['name']);?></div>                    <?php echo VIC_DateTranslate($fight['startDate']); ?>                </td>                <td>                    <?php echo esc_html($my_pick['total_kill']);?>                </td>                <td>                    <?php echo esc_html($my_pick['finish']);?>                </td>                <td>                    <?php echo esc_html($my_pick['points']);?>                </td>                <td>                    <?php if(!empty($my_pick['image'])):?>                        <a href="<?php echo esc_url($my_pick['image_url']);?>" target="_blank"><?php echo esc_html(__('View', 'victorious'))?></a>                    <?php endif;?>                </td>            </tr>        <?php endforeach;?>    </tbody>    <?php endif;?></table>