<div class="f-loading" style="display: none"></div><div class="f-user-score-summary f-clearfix">    <div>        <div class="f-rank">            <header>                <h6><?php echo esc_html(__("Position", "victorious"));?></h6>            </header>            <h1><?php echo esc_html($rank);?></h1>        </div>        <div class="f-user-info">            <div style="background-image: url('<?php echo esc_url($avatar);?>')" class="f-avatar f-left">                <?php echo esc_html($username);?>            </div>            <h2 class="f-truncate">                <?php echo esc_html($username);?>            </h2>        </div>        <div class="f-score right">            <header>                <h6><?php echo esc_html($name);?></h6>            </header>            <h1 class="f-user-score f-positive  "><?php echo esc_html($totalScore);?></h1>        </div>    </div></div><div class="f-roster">    <?php if($players != null && $is_motocross == 1):?>        <?php foreach($players as $player):            $lapResult = "";        ?>            <?php foreach($player['result'] as $lap => $lap_result):?>                <?php $lapResult.= '<li class="f-player-card-item">'.esc_html(__('Lap', 'victorious')).' '.$lap.'('.$lap_result.')</span></li>';?>            <?php endforeach;?>            <div class="f-roster-row f-finished">                <div class="f-roster-row-summary">                    <div class="f-name">                        <?php echo esc_html($player['player_name']);?>                    </div>                    <div class="f-fixture-info">                        <div>                            <p style="margin-top:3px;font-weight:bold;">                                <?php echo esc_html($player['position_name']);?>                            </p>                        </div>                    </div>                    <div class="f-player-secondary-information">                        <div class="f-player-salary">                        </div>                    </div>                    <div class="f-player-score-breakdown">                        <ul class="f-player-card">                            <?php echo esc_html($lapResult);?>                        </ul>                    </div>                    <div class="f-player-score-breakdown">                        <ul class="f-player-card">                        </ul>                    </div>                    <div class="f-score">                        <div>                            <div class="f-fixture-status f-positive f-finished" style="text-align: center">                                <?php echo esc_html($player['score']);?>                            </div>                        </div>                    </div>                </div>            </div>        <?php endforeach;?>    <?php elseif($players && $is_motocross != 1):?>        <?php foreach($players as $player):?>            <div class="f-roster-row f-finished" style="<?php echo esc_attr($leagueOptionType == 'salary' ? 'padding-left:10px;' : '');?>">                <div class="f-roster-row-summary">                    <?php if(!empty($player['player_position']) && ($leagueOptionType != 'salary') && $gameType != 'GOLFSKIN'):?>                        <div class="f-pos">                            <span title="<?php echo esc_html($player['player_position']);?>">                                <?php echo esc_html($player['player_position']);?>                            </span>                        </div>                    <?php endif;?>                    <div class="f-name">                        <?php echo esc_html($player['player_name']);?>                        <div class="f-player-salary">                            <?php echo VIC_FormatMoney($player['player_salary']);?>                        </div>                    </div>                    <?php if($player['fights'] != null && !empty($player['fights'][0])):                        $fights = $player['fights'];                        $classAway = $player['teamID'] == $fights[0]['teamID'] ? 'f-player-team-highlight' : '';                        $classHome = $player['teamID'] == $fights[1]['teamID'] ? 'f-player-team-highlight' : '';                    ?>                        <div class="f-fixture-info">                            <div>                                 <span class="f-away <?php echo esc_attr($classAway);?>">                                    <?php echo esc_html($fights[0]['nickName']);?>                                </span>                                 <?php echo esc_html($fights[0]['team1score']);?> @                                <span class="f-home <?php echo esc_attr($classHome);?>">                                    <?php echo esc_html($fights[1]['nickName']);?>                                </span>                                 <?php echo esc_html($fights[0]['team2score']);?>                            </div>                        </div>                    <?php endif;?>                    <div class="f-player-score-breakdown">                        <ul class="f-player-card">                            <?php if($player['playerdraft'] != null):?>                                <?php foreach($player['playerdraft'] as $playerdraft):?>                                    <li class="f-player-card-item">                                        <?php echo esc_html($playerdraft['points']);?>                                        <span title="<?php echo VIC_ScoringTranslate($playerdraft['scoring_alias']);?>">                                            <?php echo VIC_ScoringTranslate($playerdraft['scoring_alias']).'('.$playerdraft['score'].')';?>                                        </span>                                    </li>                                <?php endforeach;?>                            <?php endif;?>                        </ul>                    </div>                    <div class="f-score">                        <div>                            <div class="f-fixture-status f-positive f-finished">                                <?php echo esc_html($player['points']);?>                            </div>                        </div>                    </div>                </div>            </div>        <?php endforeach;?>    <?php endif; ?></div>