ss="f-player-salary">                                            <?php echo VIC_FormatMoney($player['player_salary']);?>                                        </div>                                    </div>                                    <?php endif;?>                                    <div class="f-player-score-breakdown">                                        <ul class="f-player-card">                                            <?php if(!empty($player['playerstats'])):?>                                                <?php foreach($player['playerstats'] as $playerstat):?>                                                    <li class="f-player-card-item">                                                        <?php echo esc_html($playerstat['points']);?>                                                        <span title="<?php echo VIC_ScoringTranslate($playerstat['scoring_alias']);?>">                                                            <?php echo VIC_ScoringTranslate($playerstat['scoring_alias']).'('.$playerstat['score'].')';?>                                                        </span>                                                    </li>                                                <?php endforeach;?>                                            <?php endif;?>                                        </ul>                                    </div>                                    <div class="f-score">                                        <div>                                            <div class="f-fixture-status f-positive f-finished">                                                <?php echo esc_html($player['points']);?>                                            </div>                                        </div>                                    </div>                                </div>                            </div>                        <?php endforeach;?>                    </div>            