<?php     $league = $result_detail['league'];    $score = $result_detail['score'];    $scores = $result_detail['scores'];?><div class="f-loading" style="display: none"></div><div class="f-user-score-summary f-clearfix">    <div>        <div class="f-rank">            <header>                <h6><?php echo esc_html(__("Position", "victorious"));?></h6>            </header>            <h1><?php echo esc_html($score['rank']);?></h1>        </div>        <div class="f-user-info">            <div style="background-image: url('<?php echo esc_url($score['avatar']);?>')" class="f-avatar f-left">                <?php echo esc_html($score['username']);?>            </div>            <h2 class="f-truncate">                <?php echo esc_html($score['username']);?>            </h2>        </div>        <div class="f-score right">            <header>                <h6><?php echo esc_html($league['gameType'] == 'GOLFSKIN' ? esc_html(__("Skin", "victorious")) : esc_html(__("Score", "victorious")));?></h6>            </header>            <h1 class="f-user-score f-positive  "><?php echo esc_html($score['points']);?></h1>        </div>    </div></div><div class="f-roster">    <?php if($scores != null && $is_motocross == 1):?>        <?php foreach($scores as $score):            $player = $score['player'];            $position = $player['position'];            $lapResult = "";        ?>            <?php foreach($player['result'] as $lap => $lap_result):?>                <?php $lapResult.= '<li class="f-player-card-item">'.esc_html(__('Lap', 'victorious')).' '.$lap.'('.$lap_result.')</span></li>';?>            <?php endforeach;?>            <div class="f-roster-row f-finished">                <div class="f-roster-row-summary">                    <div class="f-name">                        <?php echo esc_html($player['name']);?>                    </div>                    <div class="f-fixture-info">                        <div>                            <p style="margin-top:3px;font-weight:bold;">                                <?php echo esc_html($position['name']);?>                            </p>                        </div>                    </div>                    <div class="f-player-secondary-information">                        <div class="f-player-salary">                        </div>                    </div>                    <div class="f-player-score-breakdown">                        <ul class="f-player-card">                            <?php echo esc_html($lapResult);?>                        </ul>                    </div>                    <div class="f-player-score-breakdown">                        <ul class="f-player-card">                        </ul>                    </div>                    <div class="f-score">                        <div>                            <div class="f-fixture-status f-positive f-finished" style="text-align: center">                                <?php echo esc_html($score['points']);?>                            </div>                        </div>                    </div>                </div>            </div>        <?php endforeach;?>    <?php elseif($scores && $is_motocross != 1):?>        <?php foreach($scores as $score):            $player = $score['player'];            $position = $player['position'];            $stats = $score['stats'];            $fight = $score['fight'];        ?>            <div class="f-roster-row f-finished" style="<?php echo esc_attr($leagueOptionType == 'salary' ? 'padding-left:10px;' : '');?>">                <div class="f-roster-row-summary">                    <?php if(!empty($position['name']) && ($leagueOptionType != 'salary') && $league['gameType'] != 'GOLFSKIN'):?>                        <div class="f-pos">                            <span title="<?php echo esc_html($position['name']);?>">                                <?php echo esc_html($position['name']);?>                            </span>                        </div>                    <?php endif;?>                    <div class="f-name">                        <?php echo esc_html($player['name']);?>                    </div>                    <?php if($fight != null):                        $classAway = $player['team_id'] == $fight['home_team']['teamID'] ? 'f-player-team-highlight' : '';                        $classHome = $player['team_id'] == $fight['away_team']['teamID'] ? 'f-player-team-highlight' : '';                    ?>                        <div class="f-fixture-info">                            <div>                                 <span class="f-away <?php echo esc_attr($classAway);?>">                                    <?php echo esc_html($fight['home_team']['nickName']);?>                                </span>                                 <?php echo !empty($fight['home_team']['team1score']) ? esc_html($fight['home_team']['team1score']) : 0;?> @                                <span class="f-home <?php echo esc_attr($classHome);?>">                                    <?php echo esc_html($fight['away_team']['nickName']);?>                                </span>                                 <?php echo !empty($fight['away_team']['team2score']) ? esc_html($fight['away_team']['team2score']) : 0;?>                            </div>                        </div>                    <?php endif;?>                    <div class="f-player-secondary-information">                        <div class="f-player-salary">                            <?php echo VIC_FormatMoney($player['salary']);?>                        </div>                    </div>                    <div class="f-player-score-breakdown">                        <ul class="f-player-card">                            <?php if($stats != null):?>                                <?php foreach($stats as $stat):?>                                    <li class="f-player-card-item">                                        <?php echo esc_html($stat['score']);?>                                        <span title="<?php echo VIC_ScoringTranslate($stat['alias']);?>">                                            <?php echo VIC_ScoringTranslate($stat['alias']).'('.$stat['points'].')';?>                                        </span>                                    </li>                                <?php endforeach;?>                            <?php endif;?>                        </ul>                    </div>                    <div class="f-score">                        <div>                            <div class="f-fixture-status f-positive f-finished">                                <?php echo esc_html($score['points']);?>                            </div>                        </div>                    </div>                </div>            </div>        <?php endforeach;?>    <?php endif; ?></div>