<?php if($scores != null):?>    <?php foreach($scores as $score):?>        <tr <?php if(!empty($score['current'])):?>class="f-user-highlight"<?php endif;?> href="#" onclick="return jQuery.playerdraft.userResult('<?php echo esc_attr($score['leagueID']);?>', 0, '<?php echo esc_attr($score['userID']);?>', '<?php echo esc_attr($score['username']);?>', '<?php echo esc_attr($score['avatar']);?>', '<?php echo esc_attr($score['rank']);?>', '<?php echo esc_attr($score['points']);?>', '<?php echo esc_attr($score['entry_number']);?>')" >            <td style="text-align:center">                <?php echo !empty($score['rank']) ? esc_html($score['rank']) : "-";?>            </td>            <td>                <div style="background-image: url('<?php echo esc_url($score['avatar']);?>')" class="f-avatar">                </div>                <a class="f-truncate">                    <?php echo esc_html($score['username']);?>                </a>            </td>            <?php if ($league['multi_entry'] == 1): ?>                <td class="f-text-align-center">                    <?php echo esc_html($score['entry_number']);?>                </td>            <?php endif;?>            <td class="f-num f-text-align-center">                <?php echo esc_html($score['points']);?>            </td>            <?php if($no_cash == 0):?>                <td class="f-num f-text-align-center">                    <?php echo esc_html($score['amount']);?>                </td>            <?php endif;?>        </tr>    <?php endforeach;?><?php endif; ?>