<?php if(!empty($scores)):?>    <?php foreach($scores as $score):?>        <tr <?php if($score['current']):?>class="f-user-highlight"<?php endif;?>>            <td style="text-align:center">                <?php echo esc_html($score['rank']);?>            </td>            <td>                <div style="background-image: url('<?php echo esc_url($score['avatar']);?>')" class="f-avatar">                </div>                <a class="f-truncate">                    <?php echo esc_html($score['username']);?>                </a>            </td>            <td class="f-num" style="text-align: center">                <?php echo esc_html($score['gp']);?>            </td>            <td class="f-num" style="text-align: center">                <?php echo esc_html($score['w']);?>            </td>            <td class="f-num" style="text-align: center">                <?php echo esc_html($score['l']);?>            </td>            <td class="f-num" style="text-align: center">                <?php echo esc_html($score['pf']);?>            </td>            <td class="f-num" style="text-align: center">                <?php echo esc_html($score['pa']);?>            </td>            <td class="f-num" style="text-align: center">                <?php echo esc_html($score['score']);?>            </td>            <?php if($no_cash == 0):?>                <td class="f-num" style="text-align: center">                    <?php echo esc_html($score['amount']);?>                    <?php if(!empty($score['amount_type'])):?>                    - <?php echo esc_html(strtolower($score['amount_type']));?>                    <?php endif;?>                </td>            <?php endif;?>        </tr>    <?php endforeach;?><?php endif; ?>