<script id="result-template" type="text/x-handlebars-template">	<?php if(!empty($users)):?>    <?php foreach($users as $user):?>         <tr class="tr0">            <td>                <?php echo esc_html($user['user_nicename']);?>            </td>            <td style="text-align:center">                <?php echo esc_html($user['correct_pick']);?>            </td>            <td style="text-align:center">                <?php echo esc_html($user['total_pick']);?>            </td>        </tr>    <?php endforeach;?><?php endif; ?></script><script id="paging-template" type="text/x-handlebars-template">	<?php if($num_page > 0):?>     <div id="pg" class="pg-wrapper">    <?php for($i = 1; $i <= $num_page; $i++):?>         <div class="dib <?php if($current_page == $i):?>active<?php endif;?>" onclick="jQuery.summary.loadSummary(<?php echo esc_attr($i);?>)"><?php echo esc_html($i);?></div>    <?php endfor;?>    </div><?php endif;?><script id="result-template" type="text/x-handlebars-template">	