<script type="text/javascript">    jQuery(window).load(function () {        jQuery.livescore.loadLatestDailyEvents();    });</script><div class="stats-table ng-scope" id="scores">    <div class="stats-table-top">        <?php if($sports != null):?>            <select name="sport_id" id="sport_id" class="stats-filter" onchange="jQuery.livescore.loadLatestDailyEvents();">                <?php foreach($sports as $sport):?>                    <?php if(!empty($sport['child']) && is_array($sport['child'])):?>                    <optgroup label="<?php echo esc_html($sport['name']);?>">                        <?php foreach($sport['child'] as $child_sport):?>                            <?php if($child_sport['is_active'] == 1):?>                            <option value="<?php echo esc_attr($child_sport['id']);?>" style="padding-left: 20px">                                <?php echo esc_html($child_sport['name']);?>                            </option>                            <?php endif;?>                        <?php endforeach;?>                    </optgroup>                    <?php endif;?>                <?php endforeach;?>            </select>        <?php endif;?>        <span id="daily_events"></span>    </div>    <div id="fixture_scores" class="league-scores"></div></div>