<?php VIC_GetMessage();?><?php if(!empty($allow_statistic)):?>    <div id="fsbox">        <form id="form_stats" method="post">            <input id="sort_name" type="hidden" name="sort_name" value="" />            <input id="sort_value" type="hidden" name="sort_value" value="" />            <input id="page_value" type="hidden" name="page" value="" />            <input id="login_url" type="hidden" value="<?php echo wp_login_url(); ?>" />            <?php if(!empty($sports)):?>            <div class="info_item">                <select id="sports" name="sport_id" onchange="jQuery.stats.loadStatsSportInfo();">                    <?php foreach($sports as $sport):?>                        <?php if(!empty($sport['child']) && is_array($sport['child']) AND $sport['child']):?>                            <optgroup label="<?php echo esc_html($sport['name']);?>">                                <?php foreach($sport['child'] as $org):?>                                    <option value="<?php echo esc_attr($org['id']);?>" data-isround="<?php echo esc_attr($sport['is_round']);?>">                                        <?php echo esc_html($org['name']);?>                                    </option>                                <?php endforeach; ?>                            </optgroup>                        <?php endif;?>                    <?php endforeach;?>                </select>            </div>            <?php endif;?>            <div id="sport_info" class="info_item"></div>        </form>        <div id="hidbox">            <div id="stats_data"></div>        </div>    </div>    <script>        jQuery.stats.initStat();    </script><?php else:?>    <?php echo esc_html(__("Only premium user can see this function, please contact with site admin for more detail", 'victorious'));?><?php endif;?>